import React, { useState } from 'react';
import Colors from './colors';

/**
 * 
 * @returns 1. infinite loop
 * 2. how to write if we want to invoke set only once
 * 3. how does Set work
 * 4. Component life cycle ......
 */

function Game() {
    const colors = ['red', 'green', 'blue', 'orange', 'purple'];
    const [activeColor, setActiveColor] = useState('red');
    const [previousRows, setPreviousRows] = useState([]);
    const [previousFeedback, setPreviousFeedback] = useState([]);
    const [currentRow, setCurrentRow] = useState(['','','','']);
    const [feedback, setFeedback] = useState([0,0,0,0]);
    const [activeRowIndex, setRowIndex] = useState(0);
    const [totalRows, setTotalRows] = useState(10);
    const [canCheck, setCanCheck] = useState(false);
    const [victory, setVictory] = useState(false);
    const [defeat, setDefeat] = useState(false);


    const code = [];
    for(let i=0;i<4;i++) {
        //code.push(colors[Math.floor(Math.random() * 4) + 1])
    }

    const activateColor = (color) => {
        setActiveColor(color)
      }

    const setColor = (color, id) => {
        if (victory) {
          return false
        }
        const rowId = +id.substr(1, id.indexOf('-') - 1)
        const pegId = +id.substr(id.indexOf('-') + 1)
        let activeRow = currentRow
        let isArrayFull = 0

        if (activeRowIndex === rowId && color) {
          activeRow[pegId] = color
          setCurrentRow(activeRow)

          for (let i in activeRow) {
            if (activeRow[i].length > 0) {
              isArrayFull++;
            }
          }
          if (isArrayFull >= activeRow.length) {
              setCanCheck(true)
          } else {
              setCanCheck(false)
          }
        }
      }

    const checkRow = () => {
        const activeRow = JSON.parse(JSON.stringify(currentRow))
        const secretCode = JSON.parse(JSON.stringify(code))
        const hints = feedback
        const previousHints = previousFeedback
        const previousRow = previousRows

        for (let i = 0; i < 4; i++) {
          if (activeRow[i] === secretCode[i]) {
            hints[i] = 2
            delete (activeRow[i])
            delete (secretCode[i])
          }
        }
        //partially 
        for (let i in activeRow) {
          for (let j in secretCode) {
            if (activeRow[i] === secretCode[j]) {
              hints[i] = 1
              delete (activeRow[i])
              delete (secretCode[j])
            }
          }
        }

        let win = true
        for (let i in hints) {
          if (hints[i] < 2) {
            win = false;
            break;
          }
        }

        let loss = defeat;
        if (activeRowIndex >= totalRows-1) {
          loss = true;
        }

        /* updating board */
        previousHints.push(hints)
        previousRow.push(activeRow)

        setFeedback([0, 0, 0, 0])
        setRowIndex(activeRowIndex+1)
        setPreviousFeedback(previousHints)
        setCurrentRow(['','','',''])
        setPreviousRows(previousRow)
        setCanCheck(false)
        setVictory(win)
        setDefeat(loss)

      }
    const newGame = () => {

        const secretCode = []
        for (let i = 0; i < 4; i++) {
            secretCode.push(this.state.colors[Math.floor(Math.random() * 4) + 1])
        }
        this.code = secretCode
        console.log('new Game')

        setRowIndex(0)
        setPreviousRows([])
        setPreviousFeedback([])
        setCurrentRow(['','','',''])
        setFeedback([0,0,0,0])
        setCanCheck(false)
        setVictory(false)
        setDefeat(false)
      }

    return (
        <div className='game'>
            <p>HIIIIIIIIII</p>
            <h1>hi </h1>

          <Colors
            list={colors}
            activeColor={activeColor}
            action={activateColor} />
        </div>
      );  

}

export default Game;
