import React, { useState } from 'react';
import Board from './board';
import Colors from './colors';
import Solution from './solution';

/**
 * 
 * @returns 1. infinite loop
 * 2. how to write if we want to invoke set only once
 * 3. how does Set work
 * 4. Component life cycle ......
 */


function Game() {
    const colors = ['red', 'green', 'blue', 'orange', 'purple'];
    const code = [];
    for(let i=0;i<4;i++) {
        code.push(colors[Math.floor(Math.random() * 4) + 1])
    }
    //let currentRow = ['','','',''];
    const [activeColor, setActiveColor] = useState('red');
    const [currentRow, setCurrentRow] = useState(['','','','']);
    const [canCheck, setCanCheck] = useState(false);
    const [previousRows, setPreviousRows] = useState([]);
    const [previousFeedback, setPreviousFeedback] = useState([]);
    const [feedback, setFeedback] = useState([0,0,0,0]);
    const [activeRowIndex, setRowIndex] = useState(0);
    const [totalRows, setTotalRows] = useState(10);
    const [victory, setVictory] = useState(false);
    const [defeat, setDefeat] = useState(false);
    const gameRow = {
      colors: colors,
      activeColor: activeColor,
      activeRowIndex: activeRowIndex,
      totalRows : totalRows,
      previousRows: previousRows,
      previousFeedback: previousFeedback,
      currentRow: currentRow,
      Feedback: feedback,
      code: code,
      canCheck: canCheck,
      victory: victory,
      defeat: defeat,
    }

    const activateColor = (color) => {
        //gameRow.activeColor = color
        setActiveColor(color)
      }
      const setColor = (color, id) => {
        if (gameRow.victory) {
          console.log("loose");
          return false;
        }
        const rowId = +id.substr(1, id.indexOf("-") - 1);
        console.log("row");
        console.log(rowId);
        const pegId = +id.substr(id.indexOf("-") + 1);
        console.log("peg");
        console.log(pegId);
        let activeRow = currentRow;
        let isArrayFull = 0;

        if (gameRow.activeRowIndex === rowId && color) {
          activeRow[pegId] = color;
          console.log(activeRow);
          setCurrentRow(activeRow);
          console.log(currentRow);
          //gameRow.currentRow = activeRow

          for (let i in activeRow) {
            console.log("length");
            console.log(activeRow[i].length);
            if (activeRow[i].length > 0) {
              isArrayFull++;
            }
          }
          console.log("array");
          console.log(isArrayFull);
          if (isArrayFull >= activeRow.length) {
            console.log("yes");
            setCanCheck(true);
            //gameRow.canCheck = true
          } else {
            setCanCheck(false);
            //gameRow.canCheck = false
          }
        }
      };    

    // const setColor = (color, id) => {
    //     if (gameRow.victory) {
    //       console.log("loose")
    //       return false
    //     }
    //     const rowId = +id.substr(1, id.indexOf('-') - 1);
    //     console.log("row");
    //     console.log(rowId);
    //     const pegId = +id.substr(id.indexOf('-') + 1);
    //     console.log("peg");
    //     console.log(pegId);
    //     let activeRow = gameRow.currentRow;
    //     let isArrayFull = 0;

    //     if (gameRow.activeRowIndex === rowId && color) {
    //       //gameRow.currentRow[pegId] = color
    //       activeRow[pegId] = color
    //       console.log(activeRow);
    //       //currentRow = activeRow;
    //       //setCurrentRow(activeRow)
    //       console.log(currentRow);
    //       gameRow.currentRow = activeRow

    //       for (let i in activeRow) {
    //         console.log("length");
    //         console.log(activeRow[i].length);
    //         if (activeRow[i].length > 0) {
    //           isArrayFull++;
    //         }
    //       }
    //       console.log("array");
    //       console.log(isArrayFull);
    //       if (isArrayFull >= activeRow.length) {
    //           console.log("yes");
    //           setCanCheck(true)
    //           //gameRow.canCheck = true
    //       } else {
    //         setCanCheck(false)
    //         //gameRow.canCheck = false
    //       }
    //     }
    //   }

    const checkRow = () => {
        const activeRow = JSON.parse(JSON.stringify(gameRow.currentRow))
        const secretCode = JSON.parse(JSON.stringify(gameRow.code))
        const hints = gameRow.Feedback
        const previousHints = gameRow.previousFeedback
        const previousRow = gameRow.previousRows

        for (let i = 0; i < 4; i++) {
          if (activeRow[i] === secretCode[i]) {
            hints[i] = 2
            delete (activeRow[i])
            delete (secretCode[i])
          }
        }
        //partially 
        for (let i in activeRow) {
          for (let j in secretCode) {
            if (activeRow[i] === secretCode[j]) {
              hints[i] = 1
              delete (activeRow[i])
              delete (secretCode[j])
            }
          }
        }
        hints.sort((a, b) => (b - a))

        let win = true
        for (let i in hints) {
          if (hints[i] < 2) {
            win = false;
            break;
          }
        }

        let loss = gameRow.defeat;
        if (gameRow.activeRowIndex >= gameRow.totalRows-1) {
          loss = true;
        }

        /* updating board */
        previousHints.push(hints)
        previousRow.push(activeRow)

        // this.setState({
        //   Feedback: [0, 0, 0, 0],
        //   activeRowIndex: gameRow.activeRowIndex + 1,
        //   previousHints: gameRow.previousFeedback,
        //   currentRow: ['', '', '', ''],
        //   previousRows: previousRow,
        //   canCheck: false,
        //   victory: win,
        //   defeat: loss,
        // })

        setFeedback([0, 0, 0, 0])
        setRowIndex(activeRowIndex+1)
        setPreviousFeedback(previousHints)
        //setCurrentRow(['','','',''])
        setPreviousRows(previousRow)
        setCanCheck(false)
        setVictory(win)
        setDefeat(loss)

      }
    const newGame = () => {

        const secretCode = []
        for (let i = 0; i < 4; i++) {
            secretCode.push(this.state.colors[Math.floor(Math.random() * 4) + 1])
        }
        this.code = secretCode
        console.log('new Game')

        setRowIndex(0)
        setPreviousRows([])
        setPreviousFeedback([])
        //setCurrentRow(['','','',''])
        setFeedback([0,0,0,0])
        setCanCheck(false)
        setVictory(false)
        setDefeat(false)
        setTotalRows(10)
      }

    return (
        <div className='game'>
            {/* <p>HIIIIIIIIII</p>
            <h1>hi </h1> */}

          <Colors
            list={colors}
            activeColor={activeColor}
            action={activateColor} />

          <Board
            state={gameRow}
            pegAction={setColor}
            checkAction={checkRow} />

        {/* <p className='msg'> {msg} </p>            */}
          <Solution
            state={gameRow}
            newGame={newGame} />
          </div>
      );  

}

export default Game;
